import {React, useState, useEffect} from 'react';
import './styles.css';
import Layout from '../../components-commons/Layout/Layout.js';
import CardProduct from '../../components-commons/Card-Product/CardProduct.js';

const FavPage = (props) =>{


  const [listFav, setListFav] = useState([]);
  const [favorites, setFavorites] = useState([]);
  useEffect(()=>{
    fetchFavId();
    fetchCategory(favorites);
  });

  const fetchFavId = async () => {
    const data = await fetch(`http://127.0.0.1:3200/favorito/consult/1193098162`); //id;
    const listItem = await data.json();
    setFavorites(listItem.data);
  }

  const fetchCategory = async (list) => {
    let auxList = [];
    list.map(async (e)=>{
      const data = await fetch(`http://127.0.0.1:5000/buscar/${e.k_idproducto}`); //id;
      const item = await data.json();
      auxList.push(item);
    })
    setListFav(auxList);
  }

  return(
    <Layout pos="relative">
      <section className="product-section">
        <div className="title">
          <h2> Mis Favoritos </h2>
        </div>
        <div className="products-container">
        {
          listFav.map((e)=>
            <CardProduct producto={e} key={e._id}/>
        )}
        </div>
      </section>
    </Layout>
  );
}

export default FavPage;
