import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import Layout from './components-commons/Layout/Layout.js' 
import 'bootstrap/dist/css/bootstrap.css';
import {Carousel} from 'react-bootstrap';

const Header = (props) =>{
  return(
    <header>
      <h1 align="center">
        Busca, compara y encuentra los mejores precios que se ajusten a tu bolsillo.
      </h1>
    </header>
  );
}

const Categoria = (props) =>{
  //se obtiene la lista de tarjetas ya ordenas y todo
  return(
    <section id="categorias" className="container-categoria">
      <h2> Categor√≠as Disponibles </h2>
      <article>
      {/*va las tarjetas... usando maps...*/}
       <div Style="width:100px;height:50px;border-style:solid;border-radius:4px;border-color:black;">
        <p> aqui va cada tarjeta</p>
       </div> 
      </article>
    </section>
  );
}


const Marca = (props) =>{
  let marcas = [
    {
      nombre:'Alkosto',
      img:'./assets/alkosto-slider.jpg',
    },
    {
      nombre:'Exito',
      img:'./assets/exito-slider.jpg',
    },
    {
      nombre:'Tecno Plaza',
      img:'./assets/tecnoplaza-slider.png',
    }
  ];
  return(
    <section id="Marca">
      <Carousel>
        <Carousel.Item interval={1000}>
          <img
            className="d-block w-100"
            src={marcas[0].url}
            alt="First slide"
          />
          {console.log( marcas[0].url )}
          <Carousel.Caption>
            <h3>First slide label</h3>
            <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
          </Carousel.Caption>
        </Carousel.Item>
      </Carousel>
    </section>
  );
}



const Home = (props)=>{
  return(
    <Layout>
      <Header/>
      <Categoria/>
      <Marca/>
    </Layout>
  );
}

ReactDOM.render(
  <React.StrictMode>
    <Home />
  </React.StrictMode>,
  document.getElementById('root')
);
